//Пишемо власний плагін для виводу сповіщень на екран
export default {
    install(Vue, options) {
        Vue.prototype.$message = function (html) {
            M.toast({html})
        }

        Vue.prototype.$error = function (html) {
            M.toast({html: `[Помилка]: ${html}`})
        }
    }
}
